const particleContainer=document.getElementById("particles"),Nparticles=100;let particles=[];function rand(t,i){return Math.floor(Math.random()*(i-t))+t}function createParticle(t){this.id=t,this.width=rand(1,20)+"px",this.height=this.width,this.x=rand(10,90)+"%",this.delay=rand(1,60)+"s",this.duration=rand(10,60)+"s",this.html='<span class="particle" style=" width: '+this.width+"; height: "+this.height+"; left: "+this.x+"; animation-delay: "+this.duration+"; animation-duration: "+this.duration+'; "></span>'}while(particles.length<=Nparticles){let t=new createParticle(particles.length);particles.push(t),particleContainer.innerHTML+=t.html}