const particleContainer=document.getElementById("particles");const Nparticles=100;let particles=[];function rand(min,max){return Math.floor(Math.random()*(max-min))+min}function createParticle(i){this.id=i;this.width=rand(1,20)+"px";this.height=this.width;this.x=rand(10,90)+"%";this.delay=rand(1,60)+"s";this.duration=rand(10,60)+"s";this.html='<span class="particle" style=" width: '+this.width+"; height: "+this.height+"; left: "+this.x+"; animation-delay: "+this.duration+"; animation-duration: "+this.duration+'; "></span>'}while(particles.length<=Nparticles){let Particle=new createParticle(particles.length);particles.push(Particle);particleContainer.innerHTML+=Particle.html}
